mapper.select:
SELECT 
FROM DEMO_USER
WHERE USER_NAME=#{userName} AND USER_PASSWORD=#{userPassword}

mapper.update:
UPDATE DEMO_USER

    USER_NAME=#{userName},
    USER_PASSWORD=#{userPassword},
    ORG_ID=#{orgId}

  WHERE USER_ID=#{userId}

mapper.select:
SELECT a.USER_ID,a.ORG_ID,a.USER_NAME,a.USER_PASSWORD,b.ORG_NAME,b.ORG_SHORTNAME
FROM DEMO_USER a LEFT JOIN DEMO_ORGANIZATION b
ON a.ORG_ID = b.ORG_ID


        a.USER_NAME LIKE concat('%',#{userName},'%')


       and a.ORG_ID = #{orgId}


LIMIT #{beginPage},#{pageSize}

SELECT COUNT(a.USER_NAME) FROM DEMO_USER a LEFT JOIN DEMO_ORGANIZATION b ON a.ORG_ID = b.ORG_ID
a.USER_NAME LIKE concat('%',#{userName},'%')
and a.ORG_ID = #{orgId}
